import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'

const initialState = {
    boards: [],
}

export const getBoards = createAsyncThunk('/getBoards', async (params, { rejectWithValue }) => {
    try {
      const { data } = await axios.post('/login', params)
      localStorage.setItem('token', data.token)
      return data
    } catch (e) {
      return rejectWithValue(e.response.data)
    }
  })

export const boardsSlice = createSlice({
    name: 'boards',
    initialState,
    reducers: {
        register: () => {

        },
    },
    extraReducers: {
        [getBoards.pending]: (state) => {
          state.authLoading = 'loading'
        },
        [getBoards.fulfilled]: (state, action) => {
          state.loginError = ''
          state.isAuth = true
          state.userData = action.payload.userData
          state.authLoading = 'loaded'
        },
        [getBoards.rejected]: (state, action) => {
          state.loginError = action.payload.message
        },
      }
})

export const boardsReducerAction = boardsSlice.actions

export default boardsSlice.reducer
