import styles from './BoardComponent.module.scss'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { useParams } from 'react-router';
import { useEffect } from 'react';
import { getBoard } from '../../features/boardSlice';
import { getThreads, threadReducerAction } from '../../features/threadSlice';
import SpinnerComponent from '../spinner/SpinnerComponent';

function CreateThreadFormComponent({ visible, setVisible, createThreadError }) {

  const dispatch = useDispatch()

  return (
    <div className={`${visible && 'activePopUp'} popUp`}>
      <div className='popUpContent'>
        <h3 className='text-center mb-2'>Создать тред</h3>
        <div>
          <input type="text" placeholder='Название' />
          <textarea placeholder='Описание' rows="3"></textarea>
        </div>
        <div className='flex justify-between mt-6'>
          <span className='linkItem' onClick={() => setVisible(false)}>Отменить</span>
          <span className='linkItem'>Создать</span>
        </div>
        <span>{createThreadError !== '' && createThreadError}</span>
      </div>

    </div>
  );
}

export default CreateThreadFormComponent;
