  return (

    <div className={`${styles.board}`}>
      <div className={`${styles.boardTitle}`}>
        <h1>{board.name}</h1>
        <span className='linkItem'>Создать тред</span>
      </div>
      <input type="text" placeholder='Поиск' />

        {threadsLoadStatus === 'loaded' ? threads.length > 0 ?
          <>
            {threads.map(el => {
              return (
                <Link to={'2'} className={`${styles.threadListItem}`}>
                  <img src="https://proprikol.ru/wp-content/uploads/2020/09/kartinki-lyagushki-52.jpg" alt="$" />
                  <div>
                    <span>Постов: 5</span>
                    <h4>{el.name}</h4>
                    <p>{el.desc}</p>
                  </div>
                </Link>
              )
            })}
            {threadsLength > showedThreadsNum &&
              <div onClick={() => dispatch(threadReducerAction.loadMoreThreads())} className='text-center linkItem my-6'>
                Загрузить больше
              </div>
            }
          </>
          : <div className='text-center mt-4'>Треды отсутствуют</div>
          : <SpinnerComponent />
        }
    </div>
  );
}

export default BoardComponent;
