import { useDispatch, useSelector } from 'react-redux'
import { useState } from 'react';
import { Link } from 'react-router-dom'
import { useParams } from 'react-router';
import { useEffect } from 'react';
import { getBoard } from '../../features/boardSlice';
import { getThreads } from '../../features/threadSlice';

function BoardComponent() {

  const dispatch = useDispatch()
  const { boardHref } = useParams();
  useEffect(() => {
    const getBoardPromise = new Promise((resolve, reject) => {
      dispatch(getBoard({ boardHref })).then(() => {
        resolve('a');
        reject(new Error('Что-то пошло не так'));
      });
      getBoardPromise.then()
      dispatch(getThreads({ boardId: board.id, searchQuery, currentTape }))
    })
  }, [])
  const { board } = useSelector((state) => state.board)
  const { searchQuery, currentTape } = useSelector((state) => state.thread)
  useEffect(() => {


  }, [board])

  return (

    <div className={`${styles.board}`}>
      <div className={`${styles.boardTitle}`}>
        <h1>{board.name}</h1>
        <span className='linkItem'>Создать тред</span>
      </div>
      <input type="text" placeholder='Поиск' />
      <div className={`${styles.threadList}`}>
        <Link to={'2'} className={`${styles.threadListItem}`}>
          <img src="https://proprikol.ru/wp-content/uploads/2020/09/kartinki-lyagushki-52.jpg" alt="$" />
          <div>
            <span>Постов: 5</span>
            <h4>Заголовок</h4>
            <p>Текст</p>
          </div>
        </Link>
      </div>
      <div className='text-center linkItem my-8'>
        Загрузить больше
      </div>
    </div>
  );
}

export default BoardComponent;
