import styles from './BoardComponent.module.scss'
import { useDispatch, useSelector } from 'react-redux'
import { Link } from 'react-router-dom'
import { useParams } from 'react-router';
import { useEffect } from 'react';
import { getBoard } from '../../features/boardSlice';
import { getThreads, threadReducerAction } from '../../features/threadSlice';
import SpinnerComponent from '../spinner/SpinnerComponent';

function CreateThreadFormComponent({ visible, setVisible }) {

  const dispatch = useDispatch()
  const { boardHref } = useParams();
  useEffect(() => {
    dispatch(getBoard({ boardHref }))
  }, [])
  const { board, boardLoadStatus } = useSelector((state) => state.board)
  const { showedThreadsNum, threadsLength, threadsLoadStatus, searchQuery, currentTape, threads } = useSelector((state) => state.thread)
  useEffect(() => {
    if (boardLoadStatus === 'loaded') {
      dispatch(getThreads({ boardId: board.id, searchQuery, currentTape }))
    }
  }, [boardLoadStatus, currentTape, searchQuery])

  return (
    <div className={`${visible && 'opacity-100'} popUp`}>
      CreateThreadFormComponent
      <span onClick={() => setVisible(false)}>Отменить</span>
    </div>
  );
}

export default CreateThreadFormComponent;
