import styles from './BoardComponent.module.scss'
import { useDispatch, useSelector } from 'react-redux'
import { useState } from 'react';
import { Link } from 'react-router-dom'
import { useParams } from 'react-router';
import { useEffect } from 'react';
import { getBoard } from '../../features/boardSlice';
import { getThreads } from '../../features/threadSlice';

function BoardComponent() {

  const dispatch = useDispatch()
  const { boardHref } = useParams();
  useEffect(() => {
    dispatch(getBoard({ boardHref }))
  }, [])
  const { board, boardLoadStatus } = useSelector((state) => state.board)
  useEffect(() => {
    if(boardLoadStatus==='loaded') {
      dispatch(getThreads({ boardId: board.id, searchQuery, currentTape }))
    }
  }, [boardLoadStatus])
  const { showedThreadsNum, threadsLength, threadsLoadStatus, threadLoadStatus, searchQuery, currentTape, threads, thread } = useSelector((state) => state.thread)

  return (

    <div className={`${styles.board}`}>
      <div className={`${styles.boardTitle}`}>
        <h1>{board.name}</h1>
        <span className='linkItem'>Создать тред</span>
      </div>
      <input type="text" placeholder='Поиск' />
      <div className={`${styles.threadList}`}>
      {boardsLoadStatus === 'loaded' ? boards.length > 0 ?
            <>
              <div className={`${styles.boardsListItem} border-b-2 border-indigo-300`}>
                <div className={`font-medium ${styles.boardsListId}`}>Id</div>
                <div className={`font-medium ${styles.boardsListLink}`}>Доска</div>
                <div className={`font-medium ${styles.boardsListName}`}>Название</div>
                <div className={`font-medium ${styles.boardsListDesc}`}>Описание</div>
                <div className={`font-medium ${styles.boardsListPosts}`}>Постов</div>
              </div>
              {boards.map(el => {
                return (
                  <div key={el.id} className={`${styles.boardsListItem}`}>
                    <div className={`${styles.boardsListId}`}>{el.id}</div>
                    <Link to={`boards/${el.href}`} className={`linkItem ${styles.boardsListLink}`}>/{el.href}</Link>
                    <div className={`${styles.boardsListName}`}>{el.name}</div>
                    <div className={`${styles.boardsListDesc}`}>{el.desc}</div>
                    <div className={`${styles.boardsListPosts}`}>{el.posts}</div>
                  </div>
                )
              })}
              {boardsLength > showedBoardsNum &&
                <div onClick={() => dispatch(boardReducerAction.loadMoreBoards())} className='text-center linkItem my-6'>
                  Загрузить больше
                </div>
              }
            </>
            : <div className='text-center mt-4'>Доски отсутствуют</div>
            : <SpinnerComponent />
          }
      </div>
      <div className='text-center linkItem my-8'>
        Загрузить больше
      </div>
    </div>
  );
}

export default BoardComponent;
