import { useSelector, useDispatch } from 'react-redux'
import { useState } from 'react';
import { Link } from 'react-router-dom'
import styles from './ProfileComponent.module.scss'

///admin/ad93oeFFf

function AdminPanelComponent() {

  const dispatch = useDispatch()
  const { isAuth, userData } = useSelector((state) => state.auth)
  const { usersData } = useSelector((state) => state.admin)
  const [actionType, setActionType] = useState('')
  function actionHandler() {
    switch (actionType) {
      case 'findUser':
        return (
          <div>
            <input type="text" placeholder='nickname' />
            <div className={`${styles.userList}`}>
              {usersData.map(el => {
                return (
                  <div className={`${styles.userListItem}`}>
                    <div>
                      <span>{el.nickName}</span>
                      <span>ID: {el.id}</span>
                    </div>
                    <div>
                      <span>Количество созданных тредов: {el.threadsNum}</span>
                      <span></span>
                    </div>
                  </div>
                )
              })}
            </div>
          </div>
        )
        break;

      case 'addBoard':

        break;

      default: break;
    }
  }

  return (
    <div>
      {actionType !== '' && <span className='linkItem' onClick={() => setActionType('')}>Назад</span>}
      {actionType === '' &&
        <ul>
          <li className='linkItem' onClick={() => setActionType('findUser')}>Найти юзера</li>
          <li className='linkItem' onClick={() => setActionType('addBoard')}>Добавить доску</li>
        </ul>
      }
      {actionHandler()}
    </div>
  );
}

export default AdminPanelComponent;
