import { configureStore } from '@reduxjs/toolkit'
import authSlice from './features/authSlice'
import boardSlice from './features/boardsSlice'

export const store = configureStore({
  reducer: {
    auth: authSlice,
    boards: boardSlice,
    threads
  }
})
