import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from '../axios.js'

const initialState = {
    usersData: [],
    searchQuery: '',
}

export const blockUser = createAsyncThunk('/blockUser', async (params, { rejectWithValue }) => {
    try {
        const { data } = await axios.post(`/blockUser`, params)
        return data
    } catch (e) {
        return rejectWithValue(e.response.data)
    }
})

export const adminSlice = createSlice({
    name: 'admin',
    initialState,
    reducers: {
        setSearchQuery: (state, action) => {
            state.searchQuery = action.payload
        },
    },
    extraReducers: {
        [register.pending]: (state) => {
            state.authLoadingStatus = 'loading'
        },
        [register.fulfilled]: (state, action) => {
            console.log(action.payload)
            state.userData = action.payload.userData
            state.isAuth = true
            state.authLoadingStatus = 'loaded'
            state.registerError = ''
            localStorage.setItem('token', action.payload.token);
        },
        [register.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.registerError = action.payload.msg
            state.isAuth = false
            state.authLoadingStatus = 'err'
        },

        [login.pending]: (state) => {
            state.authLoadingStatus = 'loading'
        },
        [login.fulfilled]: (state, action) => {
            state.userData = action.payload.userData
            state.isAuth = true
            state.authLoadingStatus = 'loaded'
            state.loginError = ''
            localStorage.setItem('token', action.payload.token);
        },
        [login.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.loginError = action.payload.msg
            state.isAuth = false
            state.authLoadingStatus = 'err'
        },

        [checkAuth.pending]: (state) => {
            state.authLoadingStatus = 'loading'
        },
        [checkAuth.fulfilled]: (state, action) => {
            state.userData = action.payload.userData
            state.isAuth = true
            state.authLoadingStatus = 'loaded'
        },
        [checkAuth.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.isAuth = false
            state.authLoadingStatus = 'err'
        },

        [deleteUser.pending]: (state) => {
        },
        [deleteUser.fulfilled]: (state, action) => {
            localStorage.removeItem('token')
            state.userData = []
            state.isAuth = false
        },
        [deleteUser.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.isAuth = false
        },

        [updateUser.pending]: (state) => {
            state.authLoadingStatus = 'loading'
        },
        [updateUser.fulfilled]: (state, action) => {
            state.userData = action.payload.userData
            localStorage.removeItem('token')
            localStorage.setItem('token', action.payload.token)
            state.redactProfileError = ''
            state.authLoadingStatus = 'loaded'
        },
        [updateUser.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.redactProfileError = action.payload.msg
            state.authLoadingStatus = 'err'
        },

        [blockUser.pending]: (state) => {
            state.authLoadingStatus = 'loading'
        },
        [blockUser.fulfilled]: (state, action) => {
            state.authLoadingStatus = 'loaded'
        },
        [blockUser.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.authLoadingStatus = 'err'
        },
    }
})

export const authReducerAction = authSlice.actions

export default authSlice.reducer
