import { createSlice, createAsyncThunk } from '@reduxjs/toolkit'
import axios from '../axios.js'

const initialState = {
    isAuth: false
}

export const login = createAsyncThunk('/login', async (params, { rejectWithValue }) => {
    try {
        const { data } = await axios.post(`/login`, params)
        return data
    } catch (e) {
        return rejectWithValue(e.response.data)
    }
})

export const register = createAsyncThunk('/register', async (params, { rejectWithValue }) => {
    try {
        const { data } = await axios.post(`/register`, params)
        return data
    } catch (e) {
        return rejectWithValue(e.response.data)
    }
})

export const checkAuth = createAsyncThunk('/checkAuth', async (params, { rejectWithValue }) => {
    try {
        const { data } = await axios.post(`/checkAuth`, params)
        return data
    } catch (e) {
        return rejectWithValue(e.response.data)
    }
})

export const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {
        setSearchQuery: (state, action) => {
            state.searchQuery = action.payload
        },
    },
    extraReducers: {
        [getThreads.pending]: (state) => {
            state.threadsLoadStatus = 'loading'
        },
        [getThreads.fulfilled]: (state, action) => {
            state.threads = action.payload.threadsData
            state.threadsLength = action.payload.threadsDataLength
            state.showedThreadsNum = action.payload.showedThreadsNum
            state.threadsLoadStatus = 'loaded'
        },
        [getThreads.rejected]: (state, action) => {
            console.log(action.payload.msg)
            state.threadsLoadStatus = 'err'
        },

    }
})

export const authReducerAction = authSlice.actions

export default authSlice.reducer
