import { useEffect, useState } from "react";
import StartGameComponent from "./StartGameComponent";

const App = () => {

  const [isGameStart, setIsGameStart] = useState(false)

  const startGame = (complexity) => {
    let startGameTime = Date.now();
    setIsGameStart(true)
    setComplexity(complexity)
    let timerrrr = setInterval(() => {
      let delta = Date.now() - startGameTime;
      let s = Math.floor(delta / 1000)
      let m = 0
      if (s >= 60) {
        s -= 60
        m += 1
      }
      let newTimer = { minutes: m, seconds: s }
      setTimer(newTimer)
    }, 1000);
  }

  return (
    <div className="app">
      <h1>Matching Game</h1>
      {isGameStart ?
        <StartGameComponent/> :
        <div>
          <h2>Select difficulty level</h2>
          <div className="difficultySelector">
            <span onClick={() => startGame('lowComplexity')}>Low</span>
            <span onClick={() => startGame('midComplexity')}>Middle</span>
            <span onClick={() => startGame('highComplexity')}>Hard</span>
          </div>
        </div>
      }

    </div>
  );
}

export default App
