const GameScreen = ({ timeForWord, finalWordsArr }) => {

  const wordsSlideShow = () => {
    let i = 0
    while (i < finalWordsArr.length) {
      console.log('first')
      setTimeout(() => {
        return <h2>{finalWordsArr[i]}</h2>
      }, timeForWord)
      i++
    }
  }

  return (
    <div className="app">
      {wordsSlideShow()}
    </div>
  );
}

export default GameScreen;
