import { useState } from "react";
import EndGameScreen from "./EndGameScreen";
import GameScreen from "./GameScreen";
import WelcomeScreen from "./WelcomeScreen";
import { wordsArr } from "./words";

const App = () => {

  const [gameStatus, setGameStatus] = useState('welcome')
  const [finalWordsArr, setFinalWordsArr] = useState([])
  const [timeForWord, setTimeForWord] = useState(null)
  const [wordsNumber, setWordsNumber] = useState(null)
  const [welcomeScreenErrors, setWelcomeScreenErrors] = useState('')

  const startGame = () => {
    if (wordsNumber == '' || timeForWord < 1){
      return setWelcomeScreenErrors('Fill all fields')
    }
    if (wordsNumber > wordsArr.length || wordsNumber < 1){
      return setWelcomeScreenErrors('Fill in the fields correctly')
    }
  }

console.log(typeof(timeForWord), typeof(wordsNumber))

  const gameStatusHandler = () => {
    switch (gameStatus) {
      case 'welcome': return <WelcomeScreen startGame={startGame} welcomeScreenErrors={welcomeScreenErrors} setWordsNumber={setWordsNumber} setTimeForWord={setTimeForWord} />
      case 'game': return <GameScreen />
      case 'end': return <EndGameScreen />

      default: break;
    }
  }

  return (
    <div className="app">
      <h1>Words in memory game</h1>
      {gameStatusHandler()}
    </div>
  );
}

export default App;
