        <div className='wordsList'>
          {finalWordsArr.map((el, idx) => <div key={idx} className={`${el.isGuessed ? 'guessedWord' : 'unGuessedWord'}`}>{el.word}</div>)}
        </div>
      }
    </div>
  );
}

export default GameScreen;
